import React, { useState } from 'react';
import API from '../services/api';

export default function TicketForm({ eventId, onBooked }) {
  const [form, setForm] = useState({ name:'', email:'', seats:1 });
  const handleChange = e => setForm(prev=>({...prev,[e.target.name]: e.target.value}));
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post('/tickets/book', { event: eventId, ...form });
      alert('Booked successfully!');
      setForm({ name:'', email:'', seats:1 });
      if (onBooked) onBooked();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || 'Booking failed');
    }
  };
  return (
    <form onSubmit={handleSubmit} className="ticket-form">
      <h4>Book Ticket</h4>
      <label>Name<input name="name" value={form.name} onChange={handleChange} required /></label>
      <label>Email<input name="email" type="email" value={form.email} onChange={handleChange} required /></label>
      <label>Seats<input name="seats" type="number" value={form.seats} onChange={handleChange} min="1" /></label>
      <button className="btn primary" type="submit">Book</button>
    </form>
  );
}
