import React, { useEffect, useState } from 'react';

const initial = { title:'', description:'', date:'', location:'', capacity:100, price:0 };

export default function EventForm({ onCreate, editing, onUpdate, cancelEdit }) {
  const [form, setForm] = useState(initial);

  useEffect(()=>{
    if (editing) {
      setForm({
        title: editing.title || '',
        description: editing.description || '',
        date: editing.date ? new Date(editing.date).toISOString().slice(0,10) : '',
        location: editing.location || '',
        capacity: editing.capacity || 100,
        price: editing.price || 0
      });
    } else setForm(initial);
  }, [editing]);

  const handleChange = (e) => setForm(prev=>({...prev, [e.target.name]: e.target.value}));
  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = { ...form, capacity: Number(form.capacity), price: Number(form.price) };
    if (editing) onUpdate(editing._id, payload);
    else onCreate(payload);
    setForm(initial);
  };

  return (
    <div className="panel">
      <h3>{editing ? 'Edit Event' : 'Create Event'}</h3>
      <form onSubmit={handleSubmit} className="form">
        <label>Title<input name="title" value={form.title} onChange={handleChange} required /></label>
        <label>Description<textarea name="description" value={form.description} onChange={handleChange}></textarea></label>
        <label>Date<input name="date" type="date" value={form.date} onChange={handleChange} required /></label>
        <label>Location<input name="location" value={form.location} onChange={handleChange} /></label>
        <label>Capacity<input name="capacity" type="number" value={form.capacity} onChange={handleChange} /></label>
        <label>Price<input name="price" type="number" step="0.01" value={form.price} onChange={handleChange} /></label>
        <div className="actions">
          <button className="btn primary" type="submit">{editing ? 'Update' : 'Create'}</button>
          {editing && <button type="button" className="btn" onClick={cancelEdit}>Cancel</button>}
        </div>
      </form>
    </div>
  );
}
